class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        # 记录 a, b, c 在当前窗口中的数量
        char_count = {'a': 0, 'b': 0, 'c': 0}
        left = 0
        count = 0
        n = len(s)
        
        for right in range(n):
            # 扩展右边界
            char_count[s[right]] += 1
            
            # 当窗口包含所有三个字符时
            while char_count['a'] > 0 and char_count['b'] > 0 and char_count['c'] > 0:
                # 以 left 开头、right 到末尾的所有子串都有效
                count += n - right
                # 收缩左边界，继续寻找其他有效窗口
                char_count[s[left]] -= 1
                left += 1
        
        return count


if __name__ == '__main__':
    solution = Solution()
    print(solution.numberOfSubstrings("abcabc"))
    print(solution.numberOfSubstrings(""))